import React from 'react';
import { connect } from 'react-redux';
import { Button } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Ayi from '../img/ayi.png'
import paginate from '../utils/paginate';
import Pagination from '@material-ui/lab/Pagination';
import { ADD_BASKET, CHANGE_PAGE } from '../actions';


const useStyles = makeStyles((theme) => ({
    unda: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
        minWidth: 125
    },
    add: {
        marginTop: "5px",
        padding: "3px",
        textAlign: 'center',
        backgroundColor: "#1EA4CE",
        color: "#FFFF",
        width: "100%",
        fontWeight: "300",
        '&:hover': {
            marginTop: "5px",
        padding: "3px",
        textAlign: 'center',
        backgroundColor: "#1EA4CE",
        color: "#FFFF",
        width: "100%",
        fontWeight: "300",
        },


    },


    xsr: {

        margin: "auto"
    },
    imgs: {
        maxWidth: "70%",
        height: "auto"
    },
    xsa: {
        padding: "10px",
        display: "flex",

        alignItems: "center"
    },
    out:{
        backgroundColor: "white",
    },

}));

const Productsx = ({ displayArray, sortingType, filters, displayedPage, dispatch }) => {
    const cls = useStyles();
    const handlePagination = (event, value) => {

        dispatch({ type: CHANGE_PAGE, payload: { value } });
    }
    const paginatedDisplayArray = paginate(displayArray);
    const thePageArray = paginatedDisplayArray.length ? (paginatedDisplayArray[displayedPage - 1]) : ([]);
    return (
        <>
            {thePageArray.length ? (
                thePageArray.map((item) => {
                    return (
                        <Grid className={cls.out} item xs={6} sm={4} md={3} key={item.id}>
                            <Paper elevation={0} className={cls.unda}>
                                <img src={Ayi} alt="ayi" className={cls.imgs} />
                                <br />
                                <span className={cls.priceTag}>₺ {item.price}</span>
                                <br />
                                <span className={cls.nameTag}>{item.name}</span>
                                <br />
                                <Button  variant="contained" color="primary" className={cls.add} onClick={() => dispatch({ type: ADD_BASKET, payload: { item } })}>
                                    Add
                                </Button>
                            </Paper>
                        </Grid>
                    )
                })
            ) : (
                <p>No Matching Items</p>
            )}
            <Grid item xs={12} className={cls.xsa}>
                <Pagination count={paginatedDisplayArray.length ? (paginatedDisplayArray.length) : (1)} color="primary" shape="rounded" page={displayedPage} onChange={handlePagination} className={cls.xsr} />
            </Grid>

        </>
    )

}

const mapStateToProps = (state) => {
    return { displayArray: state.displayArray, sortingType: state.sortingType, filters: state.filters, displayedPage: state.displayedPage }
}

export default connect(mapStateToProps)(Productsx);
