import React from 'react';
import { connect } from 'react-redux';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import ShoppingBasketIcon from '@material-ui/icons/ShoppingBasket';
import { Box, Paper } from '@material-ui/core';
import { BASKET_TO, ADD_BASKET, REMOVE_BASKET } from '../actions';
import ButtonGroup from '@material-ui/core/ButtonGroup';
import { BorderStyle } from '@material-ui/icons';


const useStyles = makeStyles((theme) => ({

    area: {
        width: "90%",
        textAlign: 'right',
    },

    popbox: {
        width: "330px",
        position: "absolute",
        border: "3px solid #1EA4CE",
        zIndex: "2",
        textAlign: "center",
        marginLeft: "73%",
        marginTop: "5px",
    },

    leftSide: {
        width: "60%",
        float: "left",
        textAlign: "center",
        paddingTop: "5px"
    },
    editButtons: {
        float: "right",
        width: "35%",
        paddingTop: "5px",
        marginRight: "10px",
        border:"white",
        backgroundColor: "#FFF",

    },

    itemCont: {
        marginBottom: "50px",
    },
    amount: {
        color: "white",
        backgroundColor: "#1EA4CE",
        pointerEvents: "none"
    },
    priceTag: {
        fontFamily: "Helvetica",
        color: "#1EA4CE",
        fontSize: "14px",
        lineHeight: "18px",
    },
    itemdata: {
        fontFamily: "Open Sans",
        color: "#191919",
        fontSize: "14px",
        lineHeight: "18px",
    },

    total: {
        width: "70px",
        textAlign: "center",
        border: "1px solid #1EA4CE",
        color: "#1EA4CE",
        fontSize: "12px",
        lineHeight: "15px",
        margin: "10px",
        float: "right",
        padding: "5px"
    },

}));


const BasketData = ({ basket, totalPrice,  basketboolean, dispatch }) => {
    const classes = useStyles();

    return (<>
        <div >
            <div className={classes.area}>
                <Button
                    color="primary" className={classes.button} startIcon={<ShoppingBasketIcon />}
                    onClick={() => dispatch({ type: BASKET_TO })} disableElevation>
                    {Math.abs(totalPrice.toFixed(2))} TL
                </Button>
            </div>
        </div>
        { basketboolean &&
            <Paper className={classes.popbox}>{basket.length ? (
                        basket.map((item) => {
                            return (
                                <Box key={item.id} className={classes.itemCont}>
                                    <div className={classes.leftSide}>
                                        <div className={classes.itemdata}>{item.name}</div>
                                        <div className={classes.priceTag}>{item.price} TL </div>
                                    </div>
                                    <ButtonGroup size="small" aria-label="small outlined button group" className={classes.editButtons}>
                                        <Button color="primary"  onClick={() => dispatch({ type: REMOVE_BASKET, payload: { item } })}>-</Button>
                                        <Button className={classes.amount}>{item.amount}</Button>
                                        <Button color="primary"  onClick={() => dispatch({ type: ADD_BASKET, payload: { item } })}>+</Button>
                                    </ButtonGroup>
                                </Box>)
                        }
                        )
                    ) : (
                        <h4>  Basket is empty</h4>

                    )
                    }
                <div className={classes.total}> {Math.abs(totalPrice.toFixed(2))}TL</div>
            </Paper>
        }
    </>
    )
}

const mapStateToProps = (state) => {
    return { basket: state.basket, totalPrice: state.totalPrice,  basketboolean: state. basketboolean }
}

export default connect(mapStateToProps)(BasketData);
